mapper.select:
		select `id`,`request_name`,`request_type`,`request_desc`,`request_pic`,`request_src`,`create_user_id`,`create_user_name`,`create_time`,`expect_time`,`owner_id`,`request_progress`,`request_status`,`update_user_id`,`update_time` from sys_request

		  		   and id = #{id} 
		  		   and request_name = #{requestName} 
		  		   and request_type = #{requestType} 
		  		   and request_desc = #{requestDesc} 
		  		   and request_pic = #{requestPic} 
		  		   and request_src = #{requestSrc} 
		  		   and create_user_id = #{createUserId} 
		  		   and create_user_name = #{createUserName} 
		  		   and create_time = #{createTime} 
		  		   and expect_time = #{expectTime} 
		  		   and owner_id = #{ownerId} 
		  		   and request_progress = #{requestProgress} 
		  		   and request_status = #{requestStatus} 
		  		   and update_user_id = #{updateUserId} 
		  		   and update_time = #{updateTime} 




                order by id desc



			limit #{offset}, #{limit}

select count(*) from sys_request

  		   and id = #{id} 
