mapper.select:
		SELECT sys_request.*, sys_user.username FROM sys_request,sys_user where sys_request.owner_id=sys_user.user_id

		  		   and id = #{id} 
		  		   and sys_request.request_name = #{requestName} 
		  		   and sys_request.request_type = #{requestType} 
		  		   and sys_request.request_desc = #{requestDesc} 
		  		   and sys_request.request_pic = #{requestPic} 
		  		   and sys_request.request_src = #{requestSrc} 
		  		   and sys_request.create_user_id = #{createUserId} 
		  		   and sys_request.create_user_name = #{createUserName} 
		  		   and sys_request.create_time = #{createTime} 
		  		   and sys_request.expect_time = #{expectTime} 
		  		   and sys_request.owner_id = #{ownerId} 
		  		   and sys_request.request_progress = #{requestProgress} 
		  		   and sys_request.request_status = #{requestStatus} 
		  		   and sys_request.update_user_id = #{updateUserId} 
		  		   and sys_request.update_time = #{updateTime} 




                order by id desc



			limit #{offset}, #{limit}

		SELECT
		sys_request.id,
		sys_request.request_name,
		sys_request.request_type,
	    CASE
			WHEN length( trim( sys_request.request_desc ) ) > 60 THEN
			CONCAT( substr( trim( sys_request.request_desc ), 1, 30 ), '...' ) ELSE sys_request.request_desc 
		END as request_desc,
		sys_request.request_pic,
		sys_request.request_src,
		sys_request.create_user_id,
		sys_request.create_user_name,
		sys_request.create_time,
		sys_request.expect_time,
		sys_request.owner_id,
		sys_request.request_progress,
		sys_request.request_status,
		sys_request.update_user_id,
		sys_request.update_time,
		sys_user.username 
		FROM sys_request,sys_user where sys_request.owner_id=sys_user.user_id
		  	  and id = #{id} 
		  	  and sys_request.request_name = #{requestName} 
			  and sys_request.request_type = #{requestType} 
		  	  and sys_request.request_desc = #{requestDesc} 
		  	  and sys_request.request_pic = #{requestPic} 
		  	  and sys_request.request_src = #{requestSrc} 
		  	  and sys_request.create_user_id = #{createUserId} 
		  	  and sys_request.create_user_name = #{createUserName} 
		  	  and sys_request.create_time = #{createTime} 
		  	  and sys_request.expect_time = #{expectTime} 
		  	  and sys_request.owner_id = #{ownerId} 
		  	  and sys_request.request_progress = #{requestProgress} 
		  	  and sys_request.request_status = #{requestStatus} 
		  	  and sys_request.update_user_id = #{updateUserId} 
		  	  and sys_request.update_time = #{updateTime} 		


                order by id desc



			limit #{offset}, #{limit}

select count(*) from sys_request

  		   and id = #{id} 
  		   and request_name = #{requestName} 
  		   and request_type = #{requestType} 
  		   and request_desc = #{requestDesc} 
  		   and request_pic = #{requestPic} 
  		   and request_src = #{requestSrc} 
  		   and create_user_id = #{createUserId} 
  		   and create_user_name = #{createUserName} 
  		   and create_time = #{createTime} 
  		   and expect_time = #{expectTime} 
  		   and owner_id = #{ownerId} 
  		   and request_progress = #{requestProgress} 
  		   and request_status = #{requestStatus} 
  		   and update_user_id = #{updateUserId} 
  		   and update_time = #{updateTime}

mapper.update:
update sys_request
request_name = #{requestName}, request_type = #{requestType}, request_desc = #{requestDesc} , request_pic = #{requestPic}, request_src = #{requestSrc}, create_user_id = #{createUserId}, create_user_name = #{createUserName}, create_time = #{createTime}, expect_time = #{expectTime}, owner_id = #{ownerId}, request_progress = #{requestProgress}, request_status = #{requestStatus}, update_user_id = #{updateUserId}, update_time = #{updateTime}
where id = #{id}
